ns\\" ON notifications\n    FOR ALL USING (\n        EXISTS (\n            SELECT 1 FROM tier2_users \n            WHERE u_id = auth.uid() \n            AND role = 'admin'\n        )\n    )","CREATE POLICY \\"Admins can access all notification preferences\\" ON notification_preferences\n    FOR ALL USING (\n        EXISTS (\n            SELECT 1 FROM tier2_users \n            WHERE u_id = auth.uid() \n            AND role = 'admin'\n        )\n    )","CREATE POLICY \\"Admins can access all subscriptions\\" ON subscriptions\n    FOR ALL USING (\n        EXISTS (\n            SELECT 1 FROM tier2_users \n            WHERE u_id = auth.uid() \n            AND role = 'admin'\n        )\n    )","CREATE POLICY \\"Admins can access all AI logs\\" ON ai_log\n    FOR ALL USING (\n        EXISTS (\n            SELECT 1 FROM tier2_users \n            WHERE u_id = auth.uid() \n            AND role = 'admin'\n        )\n    )","CREATE POLICY \\"Admins can access all user activity\\" ON useractivity\n    FOR ALL USING (\n        EXISTS (\n            SELECT 1 FROM tier2_users \n            WHERE u_id = auth.uid() \n            AND role = 'admin'\n        )\n    )","CREATE POLICY \\"Admins can access all business reassessment\\" ON business_reassessment\n    FOR ALL USING (\n        EXISTS (\n            SELECT 1 FROM tier2_users \n            WHERE u_id = auth.uid() \n            AND role = 'admin'\n        )\n    )","-- 3. Create function to log audit events\nCREATE OR REPLACE FUNCTION log_audit_event(\n    p_table_name TEXT,\n    p_operation TEXT,\n    p_record_id UUID DEFAULT NULL,\n    p_old_values JSONB DEFAULT NULL,\n    p_new_values JSONB DEFAULT NULL\n) RETURNS VOID AS $$\nBEGIN\n    INSERT INTO audit_log (\n        table_name,\n        operation,\n        user_id,\n        record_id,\n        old_values,\n        new_values,\n        ip_address,\n        user_agent,\n        session_id\n    ) VALUES (\n        p_table_name,\n        p_operation,\n        auth.uid(),\n        p_record_id,\n        p_old_values,\n        p_new_values,\n        inet_client_addr(),\n        current_setting('request.headers', true)::json->>'user-agent',\n        auth.uid()\n    );\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER","-- 4. Create triggers for automatic audit logging on sensitive tables\n-- Example trigger for tier2_users table\nCREATE OR REPLACE FUNCTION audit_tier2_users() RETURNS TRIGGER AS $$\nBEGIN\n    IF TG_OP = 'INSERT' THEN\n        PERFORM log_audit_event('tier2_users', 'INSERT', NEW.u_id, NULL, to_jsonb(NEW));\n        RETURN NEW;\n    ELSIF TG_OP = 'UPDATE' THEN\n        PERFORM log_audit_event('tier2_users', 'UPDATE', NEW.u_id, to_jsonb(OLD), to_jsonb(NEW));\n        RETURN NEW;\n    ELSIF TG_OP = 'DELETE' THEN\n        PERFORM log_audit_event('tier2_users', 'DELETE', OLD.u_id, to_jsonb(OLD), NULL);\n        RETURN OLD;\n    END IF;\n    RETURN NULL;\nEND;\n$$ LANGUAGE plpgsql","-- Create trigger on tier2_users\nDROP TRIGGER IF EXISTS audit_tier2_users_trigger ON tier2_users","CREATE TRIGGER audit_tier2_users_trigger\n    AFTER INSERT OR UPDATE OR DELETE ON tier2_users\n    FOR EACH ROW EXECUTE FUNCTION audit_tier2_users()","-- 5. Create indexes for better audit log performance\nCREATE INDEX IF NOT EXISTS idx_audit_log_user_id ON audit_log(user_id)","CREATE INDEX IF NOT EXISTS idx_audit_log_table_name ON audit_log(table_name)","CREATE INDEX IF NOT EXISTS idx_audit_log_timestamp ON audit_log(timestamp)","CREATE INDEX IF NOT EXISTS idx_audit_log_operation ON audit_log(operation)","-- 6. Add role column to tier2_users if it doesn't exist\nDO $$ \nBEGIN\n    IF NOT EXISTS (\n        SELECT 1 FROM information_schema.columns \n        WHERE table_name = 'tier2_users' AND column_name = 'role'\n    ) THEN\n        ALTER TABLE tier2_users ADD COLUMN role TEXT DEFAULT 'user' CHECK (role IN ('user', 'admin', 'support'));\n    END IF;\nEND $$","-- 7. Create view for admin dashboard\nCREATE OR REPLACE VIEW admin_dashboard AS\nSELECT \n    'tier2_users' as table_name,\n    COUNT(*) as record_count,\n    MAX(created_at) as last_updated\nFROM tier2_users\nUNION ALL\nSELECT \n    'growth_users' as table_name,\n    COUNT(*) as record_count,\n    MAX(created_at) as last_updated\nFROM growth_users\nUNION ALL\nSELECT \n    'subscriptions' as table_name,\n    COUNT(*) as record_count,\n    MAX(created_at) as last_updated\nFROM subscriptions\nUNION ALL\nSELECT \n    'audit_log' as table_name,\n    COUNT(*) as record_count,\n    MAX(timestamp) as last_updated\nFROM audit_log","-- Grant access to admin dashboard view\nGRANT SELECT ON admin_dashboard TO authenticated"}	enhance_security_rls
20240321000007	{"-- Migration: Disable RLS on Public Tables\n-- Date: 2024-03-21\n-- Description: Disable RLS on tables containing public, anonymized data that should be accessible to all users\n\n-- 1. Disable RLS on public trend and insight tables\n-- These tables contain anonymized, aggregated data that should be publicly readable\n\n-- Market and business trends (anonymized, read-only)\nALTER TABLE realtime_market_trends DISABLE ROW LEVEL SECURITY","ALTER TABLE realtime_business_trends DISABLE ROW LEVEL SECURITY","ALTER TABLE realtime_marketing_playbook DISABLE ROW LEVEL SECURITY","ALTER TABLE realtime_strategic_trends DISABLE ROW LEVEL SECURITY","-- General trends table (if it exists and contains public data)\n-- ALTER TABLE trends DISABLE ROW LEVEL SECURITY;\n\n-- 2. Disable RLS on reference and content tables\n-- These tables contain public reference data and content\n\n-- Industry reference data (public stock symbols, industry info)\nALTER TABLE industry_stock_symbols DISABLE ROW LEVEL SECURITY","-- Public content and insights (anonymized, aggregated)\nALTER TABLE scorecard_insights DISABLE ROW LEVEL SECURITY","-- Inspirational quotes (public content)\n-- ALTER TABLE inspirational_quotes DISABLE ROW LEVEL SECURITY;\n\n-- 3. Create appropriate policies for tables that need mixed access\n-- Some tables might need public read access but restricted write access\n\n-- Example: Allow public read access to trends but restrict writes to authenticated users\n-- (This is handled by the RLS disable above, but you could also use policies)\n\n-- 4. Add comments to document why RLS is disabled\nCOMMENT ON TABLE realtime_market_trends IS 'Public read access: Contains anonymized market trend data accessible to all users'","COMMENT ON TABLE realtime_business_trends IS 'Public read access: Contains anonymized business trend data accessible to all users'","COMMENT ON TABLE realtime_marketing_playbook IS 'Public read access: Contains anonymized marketing insights accessible to all users'","COMMENT ON TABLE realtime_strategic_trends IS 'Public read access: Contains anonymized strategic trend data accessible to all users'","COMMENT ON TABLE industry_stock_symbols IS 'Public read access: Contains public industry reference data accessible to all users'","COMMENT ON TABLE scorecard_insights IS 'Public read access: Contains anonymized scorecard insights accessible to all users'","-- 5. Create a view to document which tables have public access\nCREATE OR REPLACE VIEW public_tables_overview AS\nSELECT \n    schemaname,\n    tablename,\n    'Public Read Access' as access_type,\n    'RLS Disabled' as security_status,\n    'Anonymized/Reference Data' as data_type\nFROM pg_tables \nWHERE tablename IN (\n    'realtime_market_trends',\n    'realtime_business_trends', \n    'realtime_marketing_playbook',\n    'realtime_strategic_trends',\n    'industry_stock_symbols',\n    'scorecard_insights'\n)\nAND schemaname = 'public'\n\nUNION ALL\n\nSELECT \n    'public' as schemaname,\n    'user_specific_tables' as tablename,\n    'Restricted Access' as access_type,\n    'RLS Enabled' as security_status,\n    'User-specific data with RLS policies' as data_type","-- Grant access to the overview view\nGRANT SELECT ON public_tables_overview TO authenticated","GRANT SELECT ON public_tables_overview TO anon","-- 6. Create a function to check table security status\nCREATE OR REPLACE FUNCTION get_table_security_status(table_name TEXT)\nRETURNS TABLE(\n    table_name TEXT,\n    rls_enabled BOOLEAN,\n    has_policies BOOLEAN,\n    policy_count INTEGER,\n    security_level TEXT\n) AS $$\nBEGIN\n    RETURN QUERY\n    SELECT \n        t.table_name::TEXT,\n        t.rls_enabled,\n        CASE WHEN p.policy_name IS NOT NULL THEN TRUE ELSE FALSE END as has_policies,\n        COALESCE(policy_count.count, 0) as policy_count,\n        CASE \n            WHEN t.rls_enabled = FALSE THEN 'Public Access'\n            WHEN t.rls_enabled = TRUE AND p.policy_name IS NOT NULL THEN 'Protected with RLS'\n            ELSE 'RLS Enabled but No Policies'\n        END as security_level\n    FROM (\n        SELECT \n            schemaname || '.' || tablename as table_name,\n            rowsecurity as rls_enabled\n        FROM pg_tables \n        WHERE schemaname = 'public' \n        AND tablename = $1\n    ) t\n    LEFT JOIN (\n        SELECT \n            schemaname || '.' || tablename as table_name,\n            policyname as policy_name\n        FROM pg_policies \n        WHERE schemaname = 'public' \n        AND tablename = $1\n        LIMIT 1\n    ) p ON t.table_name = p.table_name\n    LEFT JOIN (\n        SELECT \n            schemaname || '.' || tablename as table_name,\n            COUNT(*) as count\n        FROM pg_policies \n        WHERE schemaname = 'public' \n        AND tablename = $1\n        GROUP BY schemaname, tablename\n    ) policy_count ON t.table_name = policy_count.table_name;\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER","-- 7. Add indexes for better performance on public tables\n-- (These tables will be accessed frequently by all users)\n\nCREATE INDEX IF NOT EXISTS idx_realtime_market_trends_timestamp \nON realtime_market_trends(timestamp DESC)","CREATE INDEX IF NOT EXISTS idx_realtime_business_trends_timestamp \nON realtime_business_trends(timestamp DESC)","CREATE INDEX IF NOT EXISTS idx_realtime_marketing_playbook_timestamp \nON realtime_marketing_playbook(timestamp DESC)","CREATE INDEX IF NOT EXISTS idx_realtime_strategic_trends_timestamp \nON realtime_strategic_trends(timestamp DESC)","CREATE INDEX IF NOT EXISTS idx_scorecard_insights_category \nON scorecard_insights(category)","CREATE INDEX IF NOT EXISTS idx_industry_stock_symbols_industry \nON industry_stock_symbols(industry)","-- 8. Create a summary of security configuration\nCOMMENT ON SCHEMA public IS 'Security Configuration:\n- User-specific tables: RLS enabled with user-based policies\n- Public tables: RLS disabled for anonymized/reference data\n- Admin tables: RLS enabled with admin override policies\n- Audit logging: Comprehensive tracking of all operations'"}	disable_rls_public_tables
20240321000008	{"-- Migration: Fix Growth Assessment RLS Policy\n-- Date: 2024-03-21\n-- Description: Add missing user policy for growth_assessment table to allow users to insert their own data\n\n-- Add user-specific policy for growth_assessment table\n-- This allows users to insert, update, and select their own assessment data\nCREATE POLICY \\"Users can access their own growth assessment\\" ON growth_assessment\n    FOR ALL USING (auth.uid()::uuid = u_id::uuid)","-- Add comment to document the policy\nCOMMENT ON POLICY \\"Users can access their own growth assessment\\" ON growth_assessment IS 'Allows users to insert, update, and select their own growth assessment data'","-- Verify the policy was created\nDO $$\nBEGIN\n    IF NOT EXISTS (\n        SELECT 1 FROM pg_policies \n        WHERE tablename = 'growth_assessment' \n        AND policyname = 'Users can access their own growth assessment'\n    ) THEN\n        RAISE EXCEPTION 'Failed to create user policy for growth_assessment table';\n    END IF;\nEND $$"}	fix_growth_assessment_rls
20240321000009	{"-- Migration: Create Email Subscriptions Table\n-- Date: 2024-03-21\n-- Description: Create table to store email newsletter subscriptions for \\"Stay Ahead of the Curve\\"\n\n-- Create email_subscriptions table\nCREATE TABLE IF NOT EXISTS email_subscriptions (\n    id SERIAL PRIMARY KEY,\n    email TEXT UNIQUE NOT NULL,\n    first_name TEXT,\n    last_name TEXT,\n    company TEXT,\n    title TEXT,\n    industry TEXT,\n    company_size TEXT,\n    revenue_range TEXT,\n    linkedin_url TEXT,\n    source TEXT DEFAULT 'blog_newsletter',\n    status TEXT DEFAULT 'active' CHECK (status IN ('active', 'unsubscribed', 'bounced')),\n    subscribed_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    unsubscribed_at TIMESTAMP WITH TIME ZONE,\n    last_email_sent_at TIMESTAMP WITH TIME ZONE,\n    email_count INTEGER DEFAULT 0,\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()\n)","-- Add comments for documentation\nCOMMENT ON TABLE email_subscriptions IS 'Stores email newsletter subscriptions for \\"Stay Ahead of the Curve\\"'","COMMENT ON COLUMN email_subscriptions.email IS 'Unique email address of the subscriber'","COMMENT ON COLUMN email_subscriptions.source IS 'Where the subscription came from (blog_newsletter, homepage, etc.)'","COMMENT ON COLUMN email_subscriptions.status IS 'Current subscription status'","COMMENT ON COLUMN email_subscriptions.email_count IS 'Number of emails sent to this subscriber'","-- Create indexes for better performance\nCREATE INDEX IF NOT EXISTS idx_email_subscriptions_email ON email_subscriptions(email)","CREATE INDEX IF NOT EXISTS idx_email_subscriptions_status ON email_subscriptions(status)","CREATE INDEX IF NOT EXISTS idx_email_subscriptions_subscribed_at ON email_subscriptions(subscribed_at)","CREATE INDEX IF NOT EXISTS idx_email_subscriptions_source ON email_subscriptions(source)","-- Enable RLS on email_subscriptions table\nALTER TABLE email_subscriptions ENABLE ROW LEVEL SECURITY","-- Policy for email_subscriptions - allow public inserts (for newsletter signups)\nCREATE POLICY \\"Allow public newsletter signups\\" ON email_subscriptions\n    FOR INSERT WITH CHECK (true)","-- Policy for email_subscriptions - allow users to view their own subscription\nCREATE POLICY \\"Users can view own subscription\\" ON email_subscriptions\n    FOR SELECT USING (email = current_setting('request.headers', true)::json->>'x-user-email' OR auth.uid() IS NOT NULL)","-- Policy for email_subscriptions - allow users to update their own subscription\nCREATE POLICY \\"Users can update own subscription\\" ON email_subscriptions\n    FOR UPDATE USING (email = current_setting('request.headers', true)::json->>'x-user-email' OR auth.uid() IS NOT NULL)","-- Policy for email_subscriptions - allow admins to access all subscriptions\nCREATE POLICY \\"Admins can access all email subscriptions\\" ON email_subscriptions\n    FOR ALL USING (\n        EXISTS (\n            SELECT 1 FROM tier2_users \n            WHERE u_id = auth.uid() \n            AND role = 'admin'\n        )\n    )","-- Create function to update the updated_at timestamp\nCREATE OR REPLACE FUNCTION update_email_subscriptions_updated_at()\nRETURNS TRIGGER AS $$\nBEGIN\n    NEW.updated_at = NOW();\n    RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql","-- Create trigger to automatically update updated_at\nCREATE TRIGGER update_email_subscriptions_updated_at_trigger\n    BEFORE UPDATE ON email_subscriptions\n    FOR EACH ROW\n    EXECUTE FUNCTION update_email_subscriptions_updated_at()","-- Create view for subscription analytics\nCREATE OR REPLACE VIEW email_subscription_analytics AS\nSELECT \n    DATE_TRUNC('day', subscribed_at) as signup_date,\n    COUNT(*) as new_subscribers,\n    COUNT(CASE WHEN status = 'active' THEN 1 END) as active_subscribers,\n    COUNT(CASE WHEN status = 'unsubscribed' THEN 1 END) as unsubscribed_count,\n    COUNT(CASE WHEN status = 'bounced' THEN 1 END) as bounced_count\nFROM email_subscriptions\nGROUP BY DATE_TRUNC('day', subscribed_at)\nORDER BY signup_date DESC","-- Grant access to analytics view\nGRANT SELECT ON email_subscription_analytics TO authenticated","-- Create function to get subscription count\nCREATE OR REPLACE FUNCTION get_email_subscription_count()\nRETURNS INTEGER AS $$\nBEGIN\n    RETURN (\n        SELECT COUNT(*) \n        FROM email_subscriptions \n        WHERE status = 'active'\n    );\nEND;\n$$ LANGUAGE plpgsql SECURITY DEFINER","-- Grant execute permission on the function\nGRANT EXECUTE ON FUNCTION get_email_subscription_count() TO authenticated","GRANT EXECUTE ON FUNCTION get_email_subscription_count() TO anon"}	create_email_subscriptions
20240321000010	{"-- Migration: Create Trial Users Table\n-- Date: 2024-03-21\n-- Description: Create table to store trial user information\n\n-- Create trial_users table\nCREATE TABLE IF NOT EXISTS trial_users (\n    id SERIAL PRIMARY KEY,\n    email TEXT UNIQUE NOT NULL,\n    first_name TEXT NOT NULL,\n    last_name TEXT,\n    company TEXT,\n    title TEXT,\n    trial_start_date TIMESTAMP WITH TIME ZONE NOT NULL,\n    trial_end_date TIMESTAMP WITH TIME ZONE NOT NULL,\n    status TEXT DEFAULT 'active' CHECK (status IN ('active', 'expired', 'converted')),\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW()\n)","-- Add comments for documentation\nCOMMENT ON TABLE trial_users IS 'Stores trial user information for 30-day free trials'","COMMENT ON COLUMN trial_users.email IS 'Unique email address of the trial user'","COMMENT ON COLUMN trial_users.trial_start_date IS 'When the trial period began'","COMMENT ON COLUMN trial_users.trial_end_date IS 'When the trial period ends (30 days from start)'","COMMENT ON COLUMN trial_users.status IS 'Current trial status: active, expired, or converted'","-- Create indexes for better performance\nCREATE INDEX IF NOT EXISTS idx_trial_users_email ON trial_users(email)","CREATE INDEX IF NOT EXISTS idx_trial_users_status ON trial_users(status)","CREATE INDEX IF NOT EXISTS idx_trial_users_trial_end_date ON trial_users(trial_end_date)","CREATE INDEX IF NOT EXISTS idx_trial_users_created_at ON trial_users(created_at)","-- Enable RLS on trial_users table\nALTER TABLE trial_users ENABLE ROW LEVEL SECURITY","-- Policy for trial_users - allow admins to access all trial users\nCREATE POLICY \\"Admins can access all trial users\\" ON trial_users\n    FOR ALL USING (\n        EXISTS (\n            SELECT 1 FROM tier2_users \n            WHERE u_id = auth.uid() \n            AND role = 'admin'\n        )\n    )","-- Policy for trial_users - allow trial users to view their own record\nCREATE POLICY \\"Trial users can view own record\\" ON trial_users\n    FOR SELECT USING (email = current_setting('request.headers', true)::json->>'x-user-email')","-- Create function to update the updated_at timestamp\nCREATE OR REPLACE FUNCTION update_trial_users_updated_at()\nRETURNS TRIGGER AS $$\nBEGIN\n    NEW.updated_at = NOW();\n    RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql","-- Create trigger to automatically update updated_at\nCREATE TRIGGER update_trial_users_updated_at_trigger\n    BEFORE UPDATE ON trial_users\n    FOR EACH ROW\n    EXECUTE FUNCTION update_trial_users_updated_at()","-- Create view for trial analytics\nCREATE OR REPLACE VIEW trial_user_analytics AS\nSELECT \n    DATE_TRUNC('day', created_at) as signup_date,\n    COUNT(*) as new_trials,\n    COUNT(CASE WHEN status = 'active' THEN 1 END) as active_trials,\n    COUNT(CASE WHEN status = 'expired' THEN 1 END) as expired_trials,\n    COUNT(CASE WHEN status = 'converted' THEN 1 END) as converted_trials,\n    ROUND(\n        COUNT(CASE WHEN status = 'converted' THEN 1 END)::DECIMAL / COUNT(*)::DECIMAL * 100, 2\n    ) as conversion_rate\nFROM trial_users\nGROUP BY DATE_TRUNC('day', created_at)\nORDER BY signup_date DESC","-- Grant access to trial analytics view\nGRANT SELECT ON trial_user_analytics TO authenticated"}	create_trial_users
