20240321000013	{"-- Migration: Add Trial Notification Tracking\n-- Date: 2024-03-21\n-- Description: Add fields to track when trial expiring notifications were sent\n\n-- Add notification tracking columns to trial_users table\nALTER TABLE trial_users \nADD COLUMN last_expiring_notification_3 TEXT DEFAULT NULL,\nADD COLUMN last_expiring_notification_1 TEXT DEFAULT NULL,\nADD COLUMN last_expiring_notification_0 TEXT DEFAULT NULL","-- Add comments for documentation\nCOMMENT ON COLUMN trial_users.last_expiring_notification_3 IS 'Date when 3-day expiring notification was last sent (YYYY-MM-DD format)'","COMMENT ON COLUMN trial_users.last_expiring_notification_1 IS 'Date when 1-day expiring notification was last sent (YYYY-MM-DD format)'","COMMENT ON COLUMN trial_users.last_expiring_notification_0 IS 'Date when day-of expiring notification was last sent (YYYY-MM-DD format)'","-- Add index for notification tracking queries\nCREATE INDEX IF NOT EXISTS idx_trial_users_notification_tracking \nON trial_users(last_expiring_notification_3, last_expiring_notification_1, last_expiring_notification_0)","-- Add status 'archived' to the status check constraint\nALTER TABLE trial_users DROP CONSTRAINT IF EXISTS trial_users_status_check","ALTER TABLE trial_users ADD CONSTRAINT trial_users_status_check \nCHECK (status IN ('active', 'expired', 'converted', 'archived'))","-- Add comment for the new status\nCOMMENT ON COLUMN trial_users.status IS 'Current trial status: active, expired, converted, or archived'"}	add_trial_notification_tracking
20240321000014	{"-- Migration: Create Assessment Delegation System\n-- Date: 2024-03-21\n-- Description: Create tables for assessment invitations and question delegations\n\n-- 1. Create assessment_invitations table\nCREATE TABLE IF NOT EXISTS assessment_invitations (\n    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n    inviter_u_id UUID NOT NULL REFERENCES tier2_users(u_id) ON DELETE CASCADE,\n    invitee_email TEXT NOT NULL,\n    invitee_name TEXT,\n    assessment_type TEXT NOT NULL CHECK (assessment_type IN (\n        'sales', 'bpm', 'tech_stack', 'strategic_maturity', 'marketing_effectiveness',\n        'ai_readiness', 'competitive_benchmarking', 'customer_experience',\n        'digital_transformation', 'leadership'\n    )),\n    invitation_token TEXT UNIQUE NOT NULL,\n    status TEXT DEFAULT 'pending' CHECK (status IN ('pending', 'completed', 'expired')),\n    expires_at TIMESTAMP WITH TIME ZONE NOT NULL,\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    completed_at TIMESTAMP WITH TIME ZONE,\n    answers JSONB,\n    score DECIMAL(3,2),\n    custom_message TEXT\n)","-- 2. Create question_delegations table\nCREATE TABLE IF NOT EXISTS question_delegations (\n    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n    delegator_u_id UUID NOT NULL REFERENCES tier2_users(u_id) ON DELETE CASCADE,\n    delegate_email TEXT NOT NULL,\n    delegate_name TEXT,\n    assessment_type TEXT NOT NULL CHECK (assessment_type IN (\n        'sales', 'bpm', 'tech_stack', 'strategic_maturity', 'marketing_effectiveness',\n        'ai_readiness', 'competitive_benchmarking', 'customer_experience',\n        'digital_transformation', 'leadership'\n    )),\n    question_keys TEXT[] NOT NULL,\n    delegation_token TEXT UNIQUE NOT NULL,\n    status TEXT DEFAULT 'pending' CHECK (status IN ('pending', 'completed', 'expired')),\n    expires_at TIMESTAMP WITH TIME ZONE NOT NULL,\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    completed_at TIMESTAMP WITH TIME ZONE,\n    answers JSONB,\n    custom_message TEXT\n)","-- 3. Create team_members table\nCREATE TABLE IF NOT EXISTS team_members (\n    id UUID PRIMARY KEY DEFAULT gen_random_uuid(),\n    owner_u_id UUID NOT NULL REFERENCES tier2_users(u_id) ON DELETE CASCADE,\n    member_email TEXT NOT NULL,\n    member_name TEXT,\n    role TEXT NOT NULL CHECK (role IN ('vp_sales', 'vp_marketing', 'manager', 'employee')),\n    permissions JSONB DEFAULT '{}',\n    created_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    updated_at TIMESTAMP WITH TIME ZONE DEFAULT NOW(),\n    UNIQUE(owner_u_id, member_email)\n)","-- 4. Add comments for documentation\nCOMMENT ON TABLE assessment_invitations IS 'Stores assessment invitations sent by business owners to team members'","COMMENT ON TABLE question_delegations IS 'Stores question delegations sent by VP-level users to team members'","COMMENT ON TABLE team_members IS 'Stores team member information for delegation management'","-- 5. Create indexes for better performance\nCREATE INDEX IF NOT EXISTS idx_assessment_invitations_inviter ON assessment_invitations(inviter_u_id)","CREATE INDEX IF NOT EXISTS idx_assessment_invitations_token ON assessment_invitations(invitation_token)","CREATE INDEX IF NOT EXISTS idx_assessment_invitations_status ON assessment_invitations(status)","CREATE INDEX IF NOT EXISTS idx_assessment_invitations_expires ON assessment_invitations(expires_at)","CREATE INDEX IF NOT EXISTS idx_assessment_invitations_type ON assessment_invitations(assessment_type)","CREATE INDEX IF NOT EXISTS idx_question_delegations_delegator ON question_delegations(delegator_u_id)","CREATE INDEX IF NOT EXISTS idx_question_delegations_token ON question_delegations(delegation_token)","CREATE INDEX IF NOT EXISTS idx_question_delegations_status ON question_delegations(status)","CREATE INDEX IF NOT EXISTS idx_question_delegations_expires ON question_delegations(expires_at)","CREATE INDEX IF NOT EXISTS idx_question_delegations_type ON question_delegations(assessment_type)","CREATE INDEX IF NOT EXISTS idx_team_members_owner ON team_members(owner_u_id)","CREATE INDEX IF NOT EXISTS idx_team_members_email ON team_members(member_email)","CREATE INDEX IF NOT EXISTS idx_team_members_role ON team_members(role)","-- 6. Enable RLS on all tables\nALTER TABLE assessment_invitations ENABLE ROW LEVEL SECURITY","ALTER TABLE question_delegations ENABLE ROW LEVEL SECURITY","ALTER TABLE team_members ENABLE ROW LEVEL SECURITY","-- 7. Create RLS policies for assessment_invitations\n-- Users can view invitations they sent\nCREATE POLICY \\"Users can view their own invitations\\" ON assessment_invitations\n    FOR SELECT USING (auth.uid()::uuid = inviter_u_id::uuid)","-- Users can create invitations\nCREATE POLICY \\"Users can create invitations\\" ON assessment_invitations\n    FOR INSERT WITH CHECK (auth.uid()::uuid = inviter_u_id::uuid)","-- Users can update their own invitations\nCREATE POLICY \\"Users can update their own invitations\\" ON assessment_invitations\n    FOR UPDATE USING (auth.uid()::uuid = inviter_u_id::uuid)","-- Public can read invitations by token (for completion)\nCREATE POLICY \\"Public can read invitations by token\\" ON assessment_invitations\n    FOR SELECT USING (true)","-- Public can update invitations by token (for completion)\nCREATE POLICY \\"Public can update invitations by token\\" ON assessment_invitations\n    FOR UPDATE USING (true)","-- 8. Create RLS policies for question_delegations\n-- Users can view delegations they sent\nCREATE POLICY \\"Users can view their own delegations\\" ON question_delegations\n    FOR SELECT USING (auth.uid()::uuid = delegator_u_id::uuid)","-- Users can create delegations\nCREATE POLICY \\"Users can create delegations\\" ON question_delegations\n    FOR INSERT WITH CHECK (auth.uid()::uuid = delegator_u_id::uuid)","-- Users can update their own delegations\nCREATE POLICY \\"Users can update their own delegations\\" ON question_delegations\n    FOR UPDATE USING (auth.uid()::uuid = delegat