UNCTION cleanup_expired_delegations()\nRETURNS void AS $$\nBEGIN\n    -- Update expired invitations\n    UPDATE assessment_invitations \n    SET status = 'expired' \n    WHERE status = 'pending' AND expires_at < NOW();\n    \n    -- Update expired delegations\n    UPDATE question_delegations \n    SET status = 'expired' \n    WHERE status = 'pending' AND expires_at < NOW();\nEND;\n$$ LANGUAGE plpgsql","-- 12. Create function to generate secure tokens\nCREATE OR REPLACE FUNCTION generate_secure_token()\nRETURNS TEXT AS $$\nBEGIN\n    RETURN encode(gen_random_bytes(32), 'hex');\nEND;\n$$ LANGUAGE plpgsql","-- 13. Add trigger to update updated_at timestamp\nCREATE OR REPLACE FUNCTION update_updated_at_column()\nRETURNS TRIGGER AS $$\nBEGIN\n    NEW.updated_at = NOW();\n    RETURN NEW;\nEND;\n$$ LANGUAGE plpgsql","CREATE TRIGGER update_team_members_updated_at\n    BEFORE UPDATE ON team_members\n    FOR EACH ROW\n    EXECUTE FUNCTION update_updated_at_column()"}	create_assessment_delegation
